new Vue({el:"#vue-app",data:{help:!1,count:0,sentances:[{pt:"Ola",en:"Hello"},{pt:"Obrigado",en:"Thanks"},{pt:"Xiao",en:"Bye"}]},methods:{prev:function(){this.count--},next:function(){this.count++},random:function(){this.count=Math.floor(Math.random()*this.sentances.length)}},computed:{sentance:function(){var t=this.help?"en":"pt";return this.sentances[this.count][t]},total:function(){return this.sentances.length}}});